        o =[]
        p = data.iloc[i].at['description']
        for j in p:
            if (j not in Stop_Word) and (j.isnumeric() is False) and j != 'های' and j != 'ای'  and  j!='ی' and j != 'ها' and j != 'ی' and len(j)>2 and j !='نمی' and j!='کتاب':
