def bag_of_words(category) :
    bag = Counter(category.iloc[0].at['description'])
    for i in range(1,len(category)):
        bag.update(category.iloc[i].at['description'])
    return bag

def training(train):
    trained = pre_processing(train)
    cat_bag = {}
    for CAT in CATEGORY:
        C = trained[trained['categories'] == CAT]
        cat_bag[CAT] = bag_of_words(C)
    return cat_bag

def px_pc(word,cat,cat_bag):
    bag = cat_bag[cat]
    if word in bag :
        return bag[word]/sum(bag.values())
    else:
        return 10**(-8)

def Naive_Bayes(data , cat_bag):
    processed_data = pre_processing(data) 
    predict = [0] * len(processed_data)
    for i in range(len(processed_data)):
        P_CATEGORIES = {}
        for cat in CATEGORY :
            P_cat = 0
            for word in processed_data.iloc[i].at['description']:
                P_cat += px_pc(word,cat,cat_bag)
            P_CATEGORIES[cat] = P_cat
        max_key = max(P_CATEGORIES, key=P_CATEGORIES.get)
        predict[i] = max_key
    return predict

